Total_region_trends &lt;- bind_rows(Overall_southeast_trend,Overall_southwest_trend, Overall_northeast_trend, Overall_midwest_trend, Overall_west_trend)

total_region_trend &lt;- ggplot(Total_region_trends, aes(x= Year, y = avg_fmr, color = region)) +
  geom_line()+
  geom_point()+
  labs(title = "Standard Trend for Overall FMR Price for Different Regions (2006-2023)",
       x = "Year",
       y = "Average Rent Price") +
  theme_classic()

print(total_region_trend)







